create table user
(
    id           bigint auto_increment comment 'id'
        primary key,
    userAccount  varchar(256)                                                                                                               not null comment '账号',
    userPassword varchar(512)                                                                                                               not null comment '密码',
    unionId      varchar(256)                                                                                                               null comment '微信开放平台id',
    mpOpenId     varchar(256)                                                                                                               null comment '公众号openId',
    userName     varchar(256)  default (concat(_utf8mb4'Zojç¨æ·', ceiling(((rand() * 90000) + 10000))))                                   null comment '用户昵称',
    userAvatar   varchar(1024) default 'https://assets.leetcode.cn/aliyun-lc-upload/default_avatar.png?x-oss-process=image%2Fformat%2Cwebp' null comment '用户头像',
    userProfile  varchar(512)  default '这家伙很懒，什么也没有留下'                                                                                      null comment '用户简介',
    userRole     varchar(256)  default 'user'                                                                                               not null comment '用户角色：user/admin/ban',
    acceptedNum  int           default 0                                                                                                    null comment '被认可的数量（通过题目数）',
    points       int           default 0                                                                                                    null comment '比赛积分',
    isDelete     tinyint       default 0                                                                                                    not null comment '是否删除',
    createTime   datetime      default CURRENT_TIMESTAMP                                                                                    not null comment '创建时间',
    updateTime   datetime      default CURRENT_TIMESTAMP                                                                                    not null on update CURRENT_TIMESTAMP comment '更新时间'
)
    comment '用户' collate = utf8mb4_unicode_ci;

create index idx_unionId
    on user (unionId);

